let mode = "";
let questions = [];
let currentIndex = 0;
let answersGiven = [];
let correctAnswers = JSON.parse(localStorage.getItem("correctAnswers") || "{}");
let timerInterval = null;
let remainingSeconds = 0;

populateCategories();

function populateCategories() {
const select = document.getElementById("categorySelect");
for (let category in data) {
let option = document.createElement("option");
option.value = category;
option.textContent = category;
select.appendChild(option);
}
}

function startStudy() { startMode("study"); }
function startTest() { startMode("test"); }
function startEdit() { startMode("edit"); }

function startMode(selectedMode) {
// ZASTAVIT TIMER PŘI KAŽDÉM PŘEPÍNÁNÍ REŽIMU
clearInterval(timerInterval);
document.getElementById("timer").textContent = "";
mode = selectedMode;
const category = document.getElementById("categorySelect").value;
questions = data[category];

if (selectedMode === "edit") {

    if (!correctAnswers[category]) {
        correctAnswers[category] = [];
    }
let firstUnset = 0;

for (let i = 0; i < questions.length; i++) {

    if (correctAnswers[category][i] === undefined ||
        correctAnswers[category][i] === null) {

        firstUnset = i;
        break;
    }

    if (i === questions.length - 1) {
        firstUnset = 0; // vše hotovo
    }
}

currentIndex = firstUnset;
  
} else {
    currentIndex = 0;
}

answersGiven = new Array(questions.length).fill(null);
// TIMER SPUSTIT JEN V TESTU
if (selectedMode === "test") {
    startTimer(20);
}


document.getElementById("menu").classList.add("hidden");
document.getElementById("app").classList.remove("hidden");
document.getElementById("result").classList.add("hidden");
document.getElementById("timer").textContent = "";

if (mode === "test") startTimer(20); // default 20 min test

showQuestion();
}

function startTimer(minutes) {
remainingSeconds = minutes * 60;
updateTimer();
timerInterval = setInterval(() => {
remainingSeconds--;
updateTimer();
if (remainingSeconds <= 0) finishTest();
}, 1000);
}

function updateTimer() {
let min = Math.floor(remainingSeconds / 60);
let sec = remainingSeconds % 60;
document.getElementById("timer").textContent = 
"Čas: " + min + ":" + (sec < 10 ? "0" : "") + sec;
}

function showQuestion() {
const q = questions[currentIndex];
document.getElementById("progress").textContent = 
"Otázka " + (currentIndex+1) + " / " + questions.length;

document.getElementById("questionBox").textContent = q.question;
const answersBox = document.getElementById("answersBox");
answersBox.innerHTML = "";

q.answers.forEach((a, i) => {
let btn = document.createElement("button");
btn.textContent = a;

if (mode === "edit") {
btn.onclick = () => setCorrect(i);
if (getCorrectIndex() === i) btn.classList.add("selected");
} else {
btn.onclick = () => selectAnswer(i, btn);
}
updateEditInfo();
answersBox.appendChild(btn);
});
}

function setCorrect(index) {
const category = document.getElementById("categorySelect").value;
if (!correctAnswers[category]) correctAnswers[category] = [];
correctAnswers[category][currentIndex] = index;
localStorage.setItem("correctAnswers", JSON.stringify(correctAnswers));
showQuestion();
}

function getCorrectIndex() {
const category = document.getElementById("categorySelect").value;
return correctAnswers[category] ? correctAnswers[category][currentIndex] : null;
}

function selectAnswer(index, btn) {
if (mode === "study") {
let correctIndex = getCorrectIndex();
if (index === correctIndex) btn.classList.add("correct");
else btn.classList.add("wrong");
}
if (mode === "test") answersGiven[currentIndex] = index;
}

function nextQuestion() {
if (currentIndex < questions.length - 1) {
currentIndex++;
showQuestion();
} else {
if (mode === "test") finishTest();
}
}

function finishTest() {
clearInterval(timerInterval);
document.getElementById("app").classList.add("hidden");
document.getElementById("result").classList.remove("hidden");

let score = 0;
let resultHTML = "<h2>Výsledek testu</h2>";

questions.forEach((q, i) => {
let correctIndex = correctAnswers[document.getElementById("categorySelect").value]?.[i];
let userIndex = answersGiven[i];

if (userIndex === correctIndex) score++;

resultHTML += "<p><b>" + (i+1) + ". " + q.question + "</b><br>";

q.answers.forEach((a, j) => {
if (j === correctIndex)
resultHTML += "<span style='color:lightgreen'>" + a + " (správně)</span><br>";
else if (j === userIndex)
resultHTML += "<span style='color:red'>" + a + " (vaše odpověď)</span><br>";
else
resultHTML += a + "<br>";
});

resultHTML += "</p>";
});

let percent = Math.round((score / questions.length) * 100);
resultHTML = "<h3>Skóre: " + score + "/" + questions.length + 
" (" + percent + "%)</h3>" + resultHTML;

document.getElementById("result").innerHTML = resultHTML;
}

function goBack() {
document.getElementById("menu").classList.remove("hidden");
document.getElementById("app").classList.add("hidden");
document.getElementById("result").classList.add("hidden");
}
function updateEditInfo() {

    if (mode !== "edit") return;

    const category = document.getElementById("categorySelect").value;
    const total = questions.length;

    if (!correctAnswers[category]) {
        correctAnswers[category] = [];
    }

    const setCount = correctAnswers[category]
        .filter(x => x !== null && x !== undefined).length;

    document.getElementById("progress").textContent =
        "Otázka " + (currentIndex+1) + " / " + total +
        " | Nastaveno: " + setCount + " / " + total;
}
function loadMETAR() {

const icao = "LKMT";

fetch(`https://corsproxy.io/?https://aviationweather.gov/api/data/metar?ids=${icao}&format=raw`)
.then(response => response.text())
.then(data => {

if (data && data.length > 0) {

document.getElementById("metarBox").innerHTML =
"<strong>METAR LKMT:</strong><br>" + data;

}
})
.catch(() => {
document.getElementById("metarBox").textContent =
"METAR se nepodařilo načíst.";
});
}

loadMETAR();
